import os
import io
import logging
import tempfile
import requests
import base64
from flask import Flask, render_template, request, Response, jsonify, flash, redirect, url_for
from flask_cors import CORS
from PIL import Image, ImageDraw, ImageFont, ImageOps
import numpy as np

# Configure logging
logging.basicConfig(level=logging.DEBUG)

app = Flask(__name__)
app.secret_key = os.environ.get("SESSION_SECRET", "marine-waste-detection-secret-key")
CORS(app, resources={r"/predict": {"origins": "*"}})

# Marine waste classes - only plastic waste detection
MARINE_CLASSES = {
    'Plastic wastes': ['Hook', 'Chain', 'Propeller', 'Can', 'Valve', 'Bottle', 'Shampoo-bottle', 'Standing-bottle', 'Drink-carton', 'Tire', 'Wall']
}

# Class colors for visualization
CLASS_COLORS = {
    'Plastic wastes': '#45B7D1'   # Blue
}

def categorize_detection(raw_class):
    """Categorize raw detection class into main categories."""
    for main_class, sub_classes in MARINE_CLASSES.items():
        if raw_class in sub_classes:
            return main_class
    return 'Plastic wastes'  # Only category now

def load_model():
    """Load the Roboflow model configuration for marine waste detection."""
    try:
        # Create Roboflow API configuration
        roboflow_config = {
            "api_url": "https://detect.roboflow.com",
            "api_key": "W6Khbwl6kDNfKx1OGBSo",
            "model_id": "debris-detection-pasan-7azav/1"
        }

        logging.info("Roboflow API configuration loaded successfully")
        return roboflow_config
    except Exception as e:
        logging.warning(f"Error loading Roboflow configuration: {str(e)}, running in demo mode")
        return "demo_mode"

def inference(model, image):
    """Perform inference on the input image using Roboflow API or demo mode."""
    try:
        if model == "demo_mode":
            # Demo mode - always show demo image with boxes
            demo_image = create_demo_image(image)
            # Convert demo image to bytes
            demo_bytes = io.BytesIO()
            demo_image.save(demo_bytes, format='JPEG')
            demo_bytes.seek(0)
            return demo_bytes.getvalue(), [], {'Plastic wastes': 6}

        # Convert image to base64 for API
        image_bytes = io.BytesIO()
        image.save(image_bytes, format='JPEG', quality=85)
        image_b64 = base64.b64encode(image_bytes.getvalue()).decode('utf-8')

        # Prepare API request with lower confidence threshold
        api_url = f"{model['api_url']}/{model['model_id']}"

        # Get confidence threshold from request (default to 0.1)
        confidence = float(request.form.get('confidence', 0.1))

        # Make API request with user-specified confidence threshold
        response = requests.post(
            api_url,
            params={
                "api_key": model['api_key'],
                "confidence": confidence,
                "overlap": 0.5
            },
            files={
                "file": ("image.jpg", base64.b64decode(image_b64), "image/jpeg")
            },
            timeout=30
        )

        if response.status_code == 200:
            result = response.json()
            logging.info(f"Roboflow API response: {len(result.get('predictions', []))} detections found")

            # Always draw detection boxes, get label display mode from request
            label_mode = request.form.get('label_mode', 'class_confidence')
            logging.info(f"Label mode received: {label_mode}")
            result_image = draw_detections(image, result, label_mode)

            # Convert to bytes
            result_bytes = io.BytesIO()
            result_image.save(result_bytes, format='JPEG')
            result_bytes.seek(0)

            # Extract and categorize detection information
            detections = []
            detection_counts = {'Fishing wastes': 0, 'Metal wastes': 0, 'Plastic wastes': 0}

            if 'predictions' in result:
                for detection in result['predictions']:
                    raw_class = detection.get('class', 'Unknown')

                    # Categorize into main classes
                    categorized_class = categorize_detection(raw_class)
                    detection_counts[categorized_class] += 1

                    detection_info = {
                        'raw_class': raw_class,
                        'class_name': categorized_class,
                        'confidence': detection.get('confidence', 0.0),
                        'bbox': [
                            detection.get('x', 0) - detection.get('width', 0) / 2,
                            detection.get('y', 0) - detection.get('height', 0) / 2,
                            detection.get('x', 0) + detection.get('width', 0) / 2,
                            detection.get('y', 0) + detection.get('height', 0) / 2
                        ]
                    }
                    detections.append(detection_info)

            return result_bytes.getvalue(), detections, detection_counts
        else:
            logging.error(f"Roboflow API error: {response.status_code} - {response.text}")
            return create_demo_image(image), [], {'Plastic wastes': 0}

    except Exception as e:
        logging.error(f"Error during Roboflow API inference: {str(e)}")
        return create_demo_image(image), [], {'Plastic wastes': 0}

def draw_detections(image, result, label_mode='class_confidence'):
    """Draw detection boxes and labels on the image."""
    try:
        # Create a copy of the image to draw on
        result_img = image.copy()
        draw = ImageDraw.Draw(result_img)

        # Try to load a font
        try:
            font = ImageFont.load_default()
        except:
            font = None

        # Draw each detection with class-specific colors
        if 'predictions' in result:
            for detection in result['predictions']:
                # Extract detection data
                x = detection.get('x', 0)
                y = detection.get('y', 0)
                width = detection.get('width', 0)
                height = detection.get('height', 0)
                confidence = detection.get('confidence', 0.0)
                raw_class = detection.get('class', 'Unknown')

                # Categorize and get color
                main_class = categorize_detection(raw_class)
                box_color = CLASS_COLORS.get(main_class, '#FFFFFF')

                # Calculate bounding box coordinates
                x1 = x - width / 2
                y1 = y - height / 2
                x2 = x + width / 2
                y2 = y + height / 2

                # Draw rectangle with class-specific color
                draw.rectangle([x1, y1, x2, y2], outline=box_color, width=3)

                # Prepare label based on display mode
                if label_mode == 'class_confidence':
                    label = f"Plastic: {confidence*100:.1f}%"
                elif label_mode == 'class_only':
                    label = "Plastic"
                elif label_mode == 'confidence_only':
                    label = f"{confidence*100:.1f}%"
                elif label_mode == 'none':
                    label = None
                else:
                    label = f"Plastic: {confidence*100:.1f}%"

                # Draw label background and text if label exists
                if label:
                    if font:
                        bbox = draw.textbbox((0, 0), label, font=font)
                        text_width = bbox[2] - bbox[0]
                        text_height = bbox[3] - bbox[1]
                    else:
                        text_width, text_height = len(label) * 6, 11

                    draw.rectangle(
                        [x1, y1 - text_height - 4, x1 + text_width + 4, y1], 
                        fill=box_color
                    )

                    # Draw label text
                    draw.text((x1 + 2, y1 - text_height - 2), label, fill="white", font=font)

        return result_img

    except Exception as e:
        logging.error(f"Error drawing detections: {str(e)}")
        return image

def create_demo_image(image):
    """Create a demo image with placeholder detection boxes."""
    try:
        from PIL import ImageDraw, ImageFont

        # Create a copy of the image to draw on
        demo_img = image.copy()
        draw = ImageDraw.Draw(demo_img)

        # Get image dimensions
        width, height = demo_img.size

        # Add demo bounding boxes and labels
        demo_detections = [
            {"label": "Demo: Bottle", "box": (width*0.2, height*0.3, width*0.4, height*0.6)},
            {"label": "Demo: Can", "box": (width*0.6, height*0.4, width*0.8, height*0.7)}
        ]

        # Draw demo boxes
        for detection in demo_detections:
            box = detection["box"]
            label = detection["label"]

            # Draw rectangle
            draw.rectangle(box, outline="red", width=3)

            # Draw label background
            try:
                font = ImageFont.load_default()
            except:
                font = None

            if font:
                bbox = draw.textbbox((0, 0), label, font=font)
                text_width = bbox[2] - bbox[0]
                text_height = bbox[3] - bbox[1]
            else:
                text_width, text_height = len(label) * 6, 11

            draw.rectangle(
                [box[0], box[1] - text_height - 4, box[0] + text_width + 4, box[1]], 
                fill="red"
            )

            # Draw label text
            draw.text((box[0] + 2, box[1] - text_height - 2), label, fill="white", font=font)

        # Convert to bytes
        image_bytes = io.BytesIO()
        demo_img.save(image_bytes, format='JPEG')
        image_bytes.seek(0)

        return image_bytes.getvalue()

    except Exception as e:
        logging.error(f"Error creating demo image: {str(e)}")
        # Return original image if demo creation fails
        image_bytes = io.BytesIO()
        image.save(image_bytes, format='JPEG')
        image_bytes.seek(0)
        return image_bytes.getvalue()

@app.route('/')
def home():
    """Home page route."""
    return render_template('index.html', 
                         classes=MARINE_CLASSES,
                         class_colors=CLASS_COLORS)

@app.route('/predict', methods=['POST'])
def predict():
    """Prediction route for processing uploaded images."""
    if request.method == 'POST':
        try:
            # Check if image file is present in the request
            if 'image' not in request.files:
                flash('No image file uploaded', 'error')
                return redirect(url_for('home'))

            image_file = request.files['image']

            # Check if file is selected
            if image_file.filename == '':
                flash('No image file selected', 'error')
                return redirect(url_for('home'))

            # Validate file type
            allowed_extensions = {'png', 'jpg', 'jpeg', 'gif', 'bmp'}
            if not ('.' in image_file.filename and 
                    image_file.filename.rsplit('.', 1)[1].lower() in allowed_extensions):
                flash('Invalid file type. Please upload an image file.', 'error')
                return redirect(url_for('home'))

            # Read the image file
            try:
                image = Image.open(image_file)
                # Fix image orientation based on EXIF data
                image = ImageOps.exif_transpose(image)
                # Convert RGBA to RGB if necessary
                if image.mode == 'RGBA':
                    image = image.convert('RGB')
            except Exception as e:
                flash(f'Error processing image: {str(e)}', 'error')
                return redirect(url_for('home'))

            # Load the YOLO model
            model = load_model()
            if model is None:
                flash('Model not available. Please ensure the model file exists.', 'error')
                return redirect(url_for('home'))
            elif model == "demo_mode":
                flash('Running in demo mode - showing sample detections. Roboflow API may be temporarily unavailable.', 'info')

            # Perform inference on the image
            result_image_bytes, detections, detection_counts = inference(model, image)

            if result_image_bytes is None:
                flash('Error during image processing', 'error')
                return redirect(url_for('home'))

            # Check if this is a request for JSON data
            if request.args.get('format') == 'json':
                return jsonify({
                    'success': True,
                    'detections': detections,
                    'detection_counts': detection_counts,
                    'total_objects': len(detections)
                })

            # Return the result image as bytes for direct image requests
            return Response(result_image_bytes, mimetype='image/jpeg')

        except Exception as e:
            logging.error(f"Error in predict route: {str(e)}")
            flash(f'An error occurred: {str(e)}', 'error')
            return redirect(url_for('home'))

@app.route('/health')
def health_check():
    """Health check endpoint."""
    model = load_model()
    model_available = model is not None and model != "demo_mode"
    demo_mode = model == "demo_mode"

    return jsonify({
        'status': 'healthy',
        'model_available': model_available,
        'demo_mode': demo_mode,
        'classes': MARINE_CLASSES,
        'message': 'Demo mode active - Roboflow API may be temporarily unavailable' if demo_mode else 'Real Roboflow marine debris detection model loaded'
    })

@app.errorhandler(404)
def page_not_found(e):
    """Handle 404 errors."""
    return render_template('index.html', error="Page not found"), 404

@app.errorhandler(500)
def internal_server_error(e):
    """Handle 500 errors."""
    return render_template('index.html', error="Internal server error"), 500

if __name__ == '__main__':
    app.run(host='0.0.0.0', port=5000, debug=True)

